.icon img {
  width: 56px;
  height: 56px;
  padding: 4px;
}
.icon span {
  color: white;
  font-size: 12px;
  height: 30px;
  display: block;
  overflow: hidden;
  word-wrap: break-word;
  text-overflow: ellipsis;
}

.panel {
  border: 1px solid #26A6F2;
  -webkit-box-shadow: 1px 1px 13px 0px rgba(87,87,87,1);
  -moz-box-shadow: 1px 1px 13px 0px rgba(87,87,87,1);
  box-shadow: 1px 1px 13px 0px rgba(87,87,87,1);
  position:fixed !important;
  border-radius: 0;
	overflow: hidden;
}
.panel-heading {
  padding: 0;
  color: white;
  font-size: 12px;
  line-height: 12px;
  background-color: #26A6F2;
  border: none;
  user-select: none;
  border-radius: 0;
}
.panel-heading .title {
  padding: 10px 8px;
  display: inline-block;
}
.panel-heading .favicon {
  width: 16px;
  height: 16px;
  margin-left: 8px;
  margin-top: -3px;
}

iframe {
  border: none;
  height: 100%;
  width: 100%;
}

.close-window {
  background: url(https://gitcode.net/swyuuki/sucai/-/raw/master/nav/close.png) no-repeat center;
  background-size: 22px 22px;
  display: inline-block;
  float: right;
  height: 32px;
  width: 42px;
  padding: 5px 10px;
}
.close-window:hover {
  background: url(https://gitcode.net/swyuuki/sucai/-/raw/master/nav/close_hover.png) no-repeat center;
  background-size: 22px 22px;
  background-color: #E81123;
}
